const ipad=window.matchMedia("screen and (max-width: 767px)"),menu=document.querySelector(".menu"),burgerButton=document.querySelector("#burger-menu");function validation(event){event.matches?burgerButton.addEventListener("click",ocultarMostrar):burgerButton.removeEventListener("click",ocultarMostrar)}function ocultarMostrar(){menu.classList.contains("is-active")?menu.classList.remove("is-active"):menu.classList.add("is-active")}ipad.addListener(validation),validation(ipad);